mapper.select:
select * from article


        category_id = #{categoryId}




            and state = #{state}


            and (state = '待审批' or state = '草稿')



    and create_user = #{userId}

ORDER BY update_time DESC

select * from article


        category_id = #{categoryId}




            and state = #{state}


            and (state = '审批成功' or state = '审批失败')



    and create_user = #{userId}

ORDER BY update_time DESC
